salted-array = [ *bstr-encoded-salted ] bstr-encoded-salted = bstr .cbor salted-entry salted-entry = salted-claim / salted-element / decoy salted-claim = [ bstr .size 16, ; 128-bit salt any, ; claim value (int / text) ; claim name ] salted-element = [ bstr .size 16, ; 128-bit salt any ; claim value ] decoy = [ bstr .size 16 ; 128-bit salt ]
aead-encrypted-array = [ *aead-encrypted ] aead-encrypted = [ bstr .size 16, ; 128-bit nonce bstr, ; the encryption ciphertext output of a ; bstr-encoded-salted bstr ; the corresponding authentication tag ]
